import React, { useRef, useState } from 'react';
import './App.css';

const App = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [predictionResult, setPredictionResult] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);

  const imgRef = useRef(null);
  const defaultImageURL = "/shaq-default.jpg";

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
        setSelectedFile(file); // Set the file variable here
      };
      reader.readAsDataURL(file);
    } else {
      setSelectedImage(defaultImageURL);
      setSelectedFile(file); // Clear the file variable here

    }
  };

  const handleImageLoad = () => {
    console.log('Image loaded successfully!');
  };

  const handleRunAnalysis = async () => {

    if (!selectedImage) {
      console.error('Image not selected.');
      return;
    }

    const formData = new FormData();
    formData.append('image', selectedFile);

    try {
      const response = await fetch('http://localhost:8000/detect_shaq', {
        method: 'POST',
        body: formData,
      });

      const result = await response.json();
      setPredictionResult(result.shaq_probability > result.no_shaq_probability
        ? 'Shaquille O\'Neal detected with ' + result.shaq_probability * 100 + '% probability!'
        : 'No Shaquille O\'Neal detected with  ' + result.no_shaq_probability * 100 + '% probability!');
    } catch (error) {
      console.error('Error during inference:', error);
    }
  };

  const handleClear = () => {
    setSelectedImage(null);
    setPredictionResult(null);
    setSelectedFile(null); // Clear the file variable here
  };

  return (
    <div id="app-container">

      <div id="title">
        < h1>Shaquille O'Neal Detector</h1>
      </div>

      <div id="author">
        <p>Author: Liam Jennings</p>
      </div>

      <div id="description">
        <p>
          This project uses a Convolutional Neural Network (CNN) trained in TensorFlow to detect whether Shaquille O'Neal is in an image.
        </p>
        <p> 
          It makes use of a React (JavaScript) frontend, and a Flask (Python) backend.
        </p>
      </div>

      <div id="upload-area">
        <img
          ref={imgRef}
          src={selectedImage || defaultImageURL}
          alt="Selected"
          style={{ maxWidth: '100%', maxHeight: '600px' }}
          onLoad={handleImageLoad} // Added onLoad event
        />
        <br />
        <input type="file" accept="image/*" onChange={handleImageChange} />
      </div>

      <div id="run-buttons">
        <button onClick={handleRunAnalysis}>Run Analysis</button>
        <button onClick={handleClear}>Clear</button>
      </div>

      <div id="results">
        <h2>Results</h2>
        {predictionResult && <p>{predictionResult}</p>}


      </div>

      <div id="footer">
        <p>Liam Jennings | ljennin2@our.ecu.edu.au</p>
      </div>


    </div>
  );
};

export default App;
