import React, { useRef, useState } from 'react';
import './App.css';

const App = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [predictionResult, setPredictionResult] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);

  const imgRef = useRef(null);
  const defaultImageURL = "/shaq-default.jpg";

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
        setSelectedFile(file);
      };
      reader.readAsDataURL(file);
    } else {
      setSelectedImage(defaultImageURL);
      setSelectedFile(null);
    }
  };

  const handleRunAnalysis = async () => {
    if (!selectedImage) {
      console.error('Image not selected.');
      return;
    }

    const formData = new FormData();
    formData.append('image', selectedFile);

    try {
      const response = await fetch('http://localhost:8000/detect_shaq', {
        method: 'POST',
        body: formData,
      });

      const result = await response.json();

      if (result.error) {
        setPredictionResult(result.error);
        return;
      }

      setPredictionResult(result.shaq_probability > result.no_shaq_probability
        ? `Shaquille O'Neal detected with ${result.shaq_probability * 100}% probability!`
        : `No Shaquille O'Neal detected with ${result.no_shaq_probability * 100}% probability!`);
    } catch (error) {
      console.error('Error during inference:', error);
    }
  };

  const handleClear = () => {
    setSelectedImage(defaultImageURL);
    setSelectedFile(null);
    setPredictionResult(null);
  };

  return (
    <div className="app-container">
      <header>
        <h1>Shaquille O'Neal Detector</h1>
      </header>

      <section className="main-content">
        <div className="upload-area">
          <label htmlFor="file-input">
            <img
              ref={imgRef}
              src={selectedImage || defaultImageURL}
              alt="Selected"
              className="selected-image"
              onLoad={() => console.log('Image loaded successfully!')}
            />
            <input
              type="file"
              id="file-input"
              accept="image/*"
              onChange={handleImageChange}
            />
            Choose Image
          </label>
        </div>


        <div className="action-buttons">
          <button onClick={handleRunAnalysis}>Run Analysis</button>
          <button onClick={handleClear}>Clear</button>
        </div>
      </section>

      <section className="results">
        <h2>Results</h2>
        {predictionResult && <p>{predictionResult}</p>}
      </section>

      <footer>
        <p>Author: Liam Jennings | Email: ljennin2@our.ecu.edu.au | LinkedIn: <a href="https://www.linkedin.com/in/liam-jennings-b54920248">Liam Jennings</a></p>
      </footer>
    </div>
  );
};

export default App;
