import React, { useRef, useState } from 'react';
import './App.css';

const App = () => {
  const [selectedImage, setSelectedImage] = useState(null);

  const imgRef = useRef(null);
  const defaultImageURL = "/shaq-default.jpg";

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    } else {
      setSelectedImage(defaultImageURL);
    }
  };

  const handleImageLoad = () => {
    // This function is called when the image is loaded
    console.log('Image loaded successfully!');
  };

  const handleRunAnalysis = async () => {
    if (!selectedImage) {
      console.error('Image not selected.');
      return;
    }

    const formData = new FormData();
    formData.append('image', selectedImage);

    try {
      const response = await fetch('http://localhost:5000/detect_shaq', {
        method: 'POST',
        body: formData,
      });

      const result = await response.json();
      setPredictionResult(result.shaq_probability > result.no_shaq_probability
        ? 'Shaquille O\'Neal detected!'
        : 'No Shaquille O\'Neal detected!');
    } catch (error) {
      console.error('Error during inference:', error);
    }
  };

  const handleClear = () => {
    setSelectedImage(null);
  };


  return (
    <div id="app-container">

      <div id="title">
        < h1>Shaquille O'Neal Detector</h1>
      </div>

      <div id="author">
        <p>Author: Your Name</p>
      </div>

      <div id="description">
        <p>
          This is a project that uses a Convolutional Neural Network (CNN) to detect Shaquille O'Neal in images.
        </p>
      </div>

      <div id="upload-area">
        <img
          ref={imgRef}
          src={selectedImage || defaultImageURL}
          alt="Selected"
          style={{ maxWidth: '100%', maxHeight: '600px' }}
          onLoad={handleImageLoad} // Added onLoad event
        />
        <br />
        <input type="file" accept="image/*" onChange={handleImageChange} />
      </div>

      <div id="run-buttons">
        <button onClick={handleRunAnalysis}>Run Analysis</button>
        <button onClick={handleClear}>Clear</button>
      </div>

      <div id="results">
        <h2>Results</h2>

      </div>

      <div id="footer">
        <p>Â© 2023 Your Name. All rights reserved.</p>
      </div>


    </div>
  );
};

export default App;
